import React, { FC } from "react";

interface PricesProps {
  regularPrice: number;
  salePrice?: number;
}

const Prices: FC<PricesProps> = ({ regularPrice, salePrice }) => {
  const formattedRegularPrice = regularPrice.toFixed(2);
  const formattedSalePrice = salePrice ? salePrice.toFixed(2) : null;

  return (
    <div className="flex items-center">
      <span className="text-lg font-semibold mr-2">${formattedRegularPrice}</span>
      {salePrice && (
        <span className="text-sm text-neutral-500 line-through">${formattedSalePrice}</span>
      )}
    </div>
  );
};

export default Prices;
