import React, { useState, Fragment, ChangeEvent, FormEvent } from 'react';
import { useLocation } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

interface Size {
    name: string;
    stock: string;
}

interface Variation {
    color: string;
    size: Size[];
    image?: File | null;
}

interface FormData {
    sku: string;
    name: string;
    price: number;
    discount: number;
    offerEnd: Date | null;
    new: boolean;
    rating: number;
    saleCount: number;
    category: string[];
    tag: string[];
    variation: Variation[];
    shortDescription: string;
    fullDescription: string;
    productImages?: File[];
}

const ProductForm: React.FC = () => {
    const { pathname } = useLocation();

    const initialFormData: FormData = {
        sku: 'asdf123',
        name: '',
        price: 0,
        discount: 0,
        offerEnd: null,
        new: false,
        rating: 0,
        saleCount: 0,
        category: [],
        tag: [],
        variation: [
            {
                color: '',
                size: [{ name: '', stock: '' }],
            },
        ],
        shortDescription: '',
        fullDescription: '',
    };

    const initialCategories = ['fashion', 'men'];

    const [formData, setFormData] = useState<FormData>(initialFormData);
    const [selectedDate, setSelectedDate] = useState<Date | null>(null);

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: name === 'new' ? value === 'true' : value,
        }));
    };

    const handleVariationImageChange = (variationIndex: number, e: ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (file) {
            setFormData(prevData => {
                const updatedVariation = [...prevData.variation];
                updatedVariation[variationIndex].image = file;
                return {
                    ...prevData,
                    variation: updatedVariation
                };
            });
        }
    };

    const handleMultipleImagesChange = (e: ChangeEvent<HTMLInputElement>) => {
        const files = e.target.files;
        if (files && files.length > 0) {
            const fileData: File[] = Array.from(files);
            setFormData((prevData) => ({
                ...prevData,
                productImages: fileData,
            }));
        }
    };

    const handleVariationChange = (
        e: ChangeEvent<HTMLInputElement>,
        variationIndex: number,
        sizeIndex: number,
        property: keyof Size
    ) => {
        const { value } = e.target;
        setFormData((prevData) => {
            const updatedVariation = [...prevData.variation];
            if (!updatedVariation[variationIndex].size) {
                updatedVariation[variationIndex].size = [];
            }
            updatedVariation[variationIndex].size[sizeIndex] = {
                ...updatedVariation[variationIndex].size[sizeIndex],
                [property]: value
            };
            return {
                ...prevData,
                variation: updatedVariation,
            };
        });
    };

    const handleAddSize = (variationIndex: number) => {
        setFormData((prevData) => {
            const updatedVariation = [...prevData.variation];
            updatedVariation[variationIndex].size.push({ name: '', stock: '' });
            return {
                ...prevData,
                variation: updatedVariation,
            };
        });
    };

    const handleCategorySelection = (selectedCategory: string | null) => {
        if (selectedCategory) {
            setFormData((prevData) => ({
                ...prevData,
                category: [...prevData.category, selectedCategory],
            }));
        }
    };

    const handleTagSelection = (selectedTag: string | null) => {
        if (selectedTag) {
            setFormData((prevData) => ({
                ...prevData,
                tag: [...prevData.tag, selectedTag],
            }));
        }
    };

    const handleDateChange = (date: Date | null) => {
        setSelectedDate(date);
        setFormData((prevData) => ({
            ...prevData,
            offerEnd: date,
        }));
    };

    const handleColorChange = (e: ChangeEvent<HTMLInputElement>, variationIndex: number) => {
        const { value } = e.target;
        setFormData((prevData) => {
            const updatedVariation = [...prevData.variation];
            updatedVariation[variationIndex].color = value;
            return {
                ...prevData,
                variation: updatedVariation,
            };
        });
    };

    const handleSave = async (e: FormEvent) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formData.variation.forEach((variation) => {
            if (variation.image) {
                formDataToSend.append('variationImage', variation.image);
                variation.image = null;
            }
        });

        if (formData.productImages && formData.productImages.length > 0) {
            formData.productImages.forEach((image) => {
                formDataToSend.append('productImages', image);
            });
        }

        const { productImages, ...newObject } = formData;
        formDataToSend.append('product', JSON.stringify(newObject));

        try {
            const response = await fetch('http://localhost:8081/api/products', {
                method: 'POST',
                body: formDataToSend,
            });

            if (response.ok) {
                setFormData(initialFormData);
                setSelectedDate(null);
                console.log('Data saved successfully!');
            } else {
                console.error('Error:', response.statusText);
            }
        } catch (error) {
            console.error('Error while saving form data to the server', error);
        }
    };

    return (
        <Fragment>
            <div className='flex justify-center'>
                <form className='bg-white p-6 rounded-lg shadow-md w-full max-w-lg' onSubmit={handleSave}>
                    <div className='mb-4'>
                        <label htmlFor='name' className='block text-gray-700'>Product Name:</label>
                        <input
                            type='text'
                            placeholder='Product Name'
                            id='name'
                            name='name'
                            value={formData.name}
                            onChange={handleChange}
                            required
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='price' className='block text-gray-700'>Price:</label>
                        <input
                            type='number'
                            placeholder='Price'
                            id='price'
                            name='price'
                            value={formData.price}
                            onChange={handleChange}
                            required
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='discount' className='block text-gray-700'>Discount:</label>
                        <input
                            type='number'
                            placeholder='Discount'
                            id='discount'
                            name='discount'
                            value={formData.discount}
                            onChange={handleChange}
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='rating' className='block text-gray-700'>Rating:</label>
                        <input
                            type='number'
                            placeholder='Rating'
                            id='rating'
                            name='rating'
                            value={formData.rating}
                            onChange={handleChange}
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='saleCount' className='block text-gray-700'>Sale Count:</label>
                        <input
                            type='text'
                            placeholder='Sale Count'
                            id='saleCount'
                            name='saleCount'
                            value={formData.saleCount}
                            onChange={handleChange}
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='offerEnd' className='block text-gray-700'>Offer End Date:</label>
                        <DatePicker
                            selected={selectedDate}
                            onChange={handleDateChange}
                            dateFormat='MM/dd/yyyy'
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label className='block text-gray-700'>Categories:</label>
                        {initialCategories.map((category) => (
                            <label key={category} className='block'>
                                <input
                                    type='checkbox'
                                    name='category'
                                    value={category}
                                    onChange={() => handleCategorySelection(category)}
                                    className='mr-2'
                                />
                                {category}
                            </label>
                        ))}
                    </div>

                    <div className='mb-4'>
                        <label className='block text-gray-700'>Tags:</label>
                        {['new', 'sale', 'popular'].map((tag) => (
                            <label key={tag} className='block'>
                                <input
                                    type='checkbox'
                                    name='tag'
                                    value={tag}
                                    onChange={() => handleTagSelection(tag)}
                                    className='mr-2'
                                />
                                {tag}
                            </label>
                        ))}
                    </div>

                    {formData.variation.map((variation, variationIndex) => (
                        <div key={variationIndex} className='mb-4'>
                            <label className='block text-gray-700'>Color:</label>
                            <input
                                type='text'
                                value={variation.color}
                                onChange={(e) => handleColorChange(e, variationIndex)}
                                className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                            />

                            {variation.size.map((size, sizeIndex) => (
                                <Fragment key={sizeIndex}>
                                    <label className='block text-gray-700'>Size Name:</label>
                                    <input
                                        type='text'
                                        value={size.name}
                                        onChange={(e) => handleVariationChange(e, variationIndex, sizeIndex, 'name')}
                                        className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                                    />
                                    <label className='block text-gray-700'>Stock:</label>
                                    <input
                                        type='number'
                                        value={size.stock}
                                        onChange={(e) => handleVariationChange(e, variationIndex, sizeIndex, 'stock')}
                                        className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                                    />
                                </Fragment>
                            ))}

                            <button
                                type='button'
                                onClick={() => handleAddSize(variationIndex)}
                                className='mt-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded'
                            >
                                Add Size
                            </button>
                        </div>
                    ))}

                    <div className='mb-4'>
                        <label className='block text-gray-700'>Upload Product Images:</label>
                        <input
                            type='file'
                            multiple
                            onChange={handleMultipleImagesChange}
                            className='mt-1 block w-full text-gray-700 px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <button
                        type='submit'
                        className='mt-4 bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded'
                    >
                        Save Product
                    </button>
                </form>
            </div>
        </Fragment>
    );
};

export default ProductForm;
