import React, { useState, useEffect } from 'react';
import { NoSymbolIcon } from "@heroicons/react/24/outline";
import NcInputNumber from "components/NcInputNumber";
import Prices from "components/Prices";
import { Helmet } from "react-helmet-async";
import { Link } from "react-router-dom";
import ButtonPrimary from "shared/Button/ButtonPrimary";
import { useCart } from "containers/CartContext";
import { CartItem } from "containers/CartContext";

const CartPage = () => {
  const { cartItems, removeFromCart, updateCartItemQuantity } = useCart();
  const [localCartItems, setLocalCartItems] = useState<CartItem[]>(cartItems);

  useEffect(() => {
    setLocalCartItems(cartItems);
  }, [cartItems]);

  const subtotal = localCartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const shippingEstimate = 5.00 * localCartItems.length;
  const taxRate = 0.1;
  const taxEstimate = parseFloat((subtotal * taxRate).toFixed(2));
  const orderTotal = parseFloat((subtotal + shippingEstimate + taxEstimate).toFixed(2));

  const handleRemove = (id: number) => {
    removeFromCart(id);
  };

  const handleQuantityChange = (id: number, newQuantity: number) => {
    setLocalCartItems((prevItems) =>
      prevItems.map(item => item.id === id ? { ...item, quantity: newQuantity } : item)
    );
    updateCartItemQuantity(id, newQuantity);
  };

  const renderProduct = (item: CartItem, index: number) => {
    const { id, image, price, name, sizes } = item;

    return (
      <div key={index} className="relative flex py-8 sm:py-10 xl:py-12 first:pt-0 last:pb-0">
        <div className="relative h-36 w-24 sm:w-32 flex-shrink-0 overflow-hidden rounded-xl bg-slate-100">
          <img
            src={"http://localhost:8081/images/" + image}
            alt={name}
            className="h-full w-full object-contain object-center"
          />
          <Link to="/product-detail" className="absolute inset-0"></Link>
        </div>
        <div className="ml-3 sm:ml-6 flex flex-1 flex-col">
          <div>
            <div className="flex justify-between">
              <div className="flex-[1.5]">
                <h3 className="text-base font-semibold">
                  <Link to="/product-detail">{name}</Link>
                </h3>
                <div className="mt-1.5 sm:mt-2.5 flex text-sm text-slate-600 dark:text-slate-300">
                  <div className="flex items-center space-x-1.5">
                    <svg className="w-4 h-4" viewBox="0 0 24 24">
                      <NoSymbolIcon />
                    </svg>
                    <span>{sizes && sizes.join(', ')}</span>
                  </div>
                </div>
              </div>
              <div className="flex-1">
                <Prices price={price} />
              </div>
            </div>
            <NcInputNumber
              defaultValue={item.quantity}
              min={1}
              max={99}
              onChange={(newValue) => handleQuantityChange(id, newValue)}
            />
          </div>
        </div>
        <button onClick={() => handleRemove(id)} className="text-red-500">
          Remove
        </button>
      </div>
    );
  };

  return (
    <div>
      <Helmet>
        <title>Cart</title>
      </Helmet>
      <div>
        {localCartItems.length > 0 ? (
          <div>
            {localCartItems.map((item, index) => renderProduct(item, index))}
            <div className="flex justify-between">
              <span>Subtotal</span>
              <span>{subtotal}</span>
            </div>
            <div className="flex justify-between">
              <span>Shipping</span>
              <span>{shippingEstimate}</span>
            </div>
            <div className="flex justify-between">
              <span>Tax</span>
              <span>{taxEstimate}</span>
            </div>
            <div className="flex justify-between font-bold">
              <span>Total</span>
              <span>{orderTotal}</span>
            </div>
            <ButtonPrimary>
              Checkout
            </ButtonPrimary>
          </div>
        ) : (
          <div>Your cart is empty</div>
        )}
      </div>
    </div>
  );
};

export default CartPage;
