import React, { createContext, useContext, useState, ReactNode } from "react";
import { Product } from "./PageCollection"; // Adjust the import path as needed

// Define types for Cart Item and Context
interface CartItem {
  id: number;
  name: string;
  price: number;
  image: string;
  quantity: number;
}

interface CartContextType {
  cart: CartItem[];
  addToCart: (product: Product) => void;
  removeFromCart: (productId: number) => void;
}

// Create the context
const CartContext = createContext<CartContextType | undefined>(undefined);

// Create the provider component
export const CartProvider = ({ children }: { children: ReactNode }) => {
  const [cart, setCart] = useState<CartItem[]>([]);

  const addToCart = (product: Product) => {
    setCart((prevCart) => [
      ...prevCart,
      { id: product.id, name: product.name, price: product.price, image: product.image, quantity: 1 },
    ]);
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart: (productId: number) => {} }}>
      {children}
    </CartContext.Provider>
  );
};

// Custom hook to use the cart context
export const useCart = () => {
  const context = useContext(CartContext);
  if (context === undefined) {
    throw new Error("useCart must be used within a CartProvider");
  }
  return context;
};
