import React, { useState, Fragment, ChangeEvent, FormEvent } from 'react';
import { useLocation } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

interface Size {
    name: string;
    stock: string;
}

interface Variation {
    color: string;
    size: Size[];
    image?: File | null;
}

interface FormData {
    sku: string;
    name: string;
    price: number;
    discount: number;
    offerEnd: Date | null;
    new: boolean;
    rating: number;
    saleCount: number;
    category: string[];
    tag: string[];
    variation: Variation[];
    shortDescription: string;
    fullDescription: string;
    productImages?: File[];
}

const ProductForm: React.FC = () => {
    const { pathname } = useLocation();

    const initialFormData: FormData = {
        sku: 'asdf123',
        name: '',
        price: 0,
        discount: 0,
        offerEnd: null,
        new: false,
        rating: 0,
        saleCount: 0,
        category: [],
        tag: [],
        variation: [
            {
                color: '',
                size: [{ name: '', stock: '' }],
            },
        ],
        shortDescription: '',
        fullDescription: '',
    };

    const initialCategories = ['fashion', 'men'];

    const [formData, setFormData] = useState<FormData>(initialFormData);
    const [selectedDate, setSelectedDate] = useState<Date | null>(null);

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: name === 'new' ? value === 'true' : value,
        }));
    };

    const handleVariationImageChange = (variationIndex: number, e: ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (file) {
            setFormData(prevData => {
                const updatedVariation = [...prevData.variation];
                updatedVariation[variationIndex].image = file;
                return {
                    ...prevData,
                    variation: updatedVariation
                };
            });
        }
    };

    const handleMultipleImagesChange = (e: ChangeEvent<HTMLInputElement>) => {
        const files = e.target.files;
        if (files && files.length > 0) {
            const fileData: File[] = Array.from(files);
            setFormData((prevData) => ({
                ...prevData,
                productImages: fileData,
            }));
        }
    };

    const handleVariationChange = (
        e: ChangeEvent<HTMLInputElement>,
        variationIndex: number,
        sizeIndex: number,
        property: keyof Size
    ) => {
        const { value } = e.target;
        setFormData((prevData) => {
            const updatedVariation = [...prevData.variation];
            const updatedSizes = updatedVariation[variationIndex].size.map((size, index) => {
                if (index === sizeIndex) {
                    return { ...size, [property]: value };
                }
                return size;
            });
            updatedVariation[variationIndex] = {
                ...updatedVariation[variationIndex],
                size: updatedSizes
            };
            return {
                ...prevData,
                variation: updatedVariation,
            };
        });
    };

    const handleAddSize = (variationIndex: number) => {
        setFormData((prevData) => {
            const updatedVariation = [...prevData.variation];
            const newSizeArray = [...updatedVariation[variationIndex].size, { name: '', stock: '' }];
            updatedVariation[variationIndex] = {
                ...updatedVariation[variationIndex],
                size: newSizeArray
            };
            return {
                ...prevData,
                variation: updatedVariation,
            };
        });
    };

    const handleCategorySelection = (selectedCategory: string | null) => {
        if (selectedCategory) {
            setFormData((prevData) => ({
                ...prevData,
                category: [...prevData.category, selectedCategory],
            }));
        }
    };

    const handleTagSelection = (selectedTag: string | null) => {
        if (selectedTag) {
            setFormData((prevData) => ({
                ...prevData,
                tag: [...prevData.tag, selectedTag],
            }));
        }
    };

    const handleDateChange = (date: Date | null) => {
        setSelectedDate(date);
        setFormData((prevData) => ({
            ...prevData,
            offerEnd: date,
        }));
    };

    const handleColorChange = (e: ChangeEvent<HTMLInputElement>, variationIndex: number) => {
        const { value } = e.target;
        const updatedFormData = { ...formData };
        if (updatedFormData.variation && updatedFormData.variation.length > variationIndex) {
            updatedFormData.variation[variationIndex].color = value;
            setFormData(updatedFormData);
        } else {
            console.error('Variation index out of range or formData.variation is undefined');
        }
    };

    const handleSave = async (e: FormEvent) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formData.variation.forEach((variation) => {
            if (variation.image) {
                formDataToSend.append('variationImage', variation.image);
                variation.image = null;
            }
        });

        if (formData.productImages && formData.productImages.length > 0) {
            formData.productImages.forEach((image) => {
                formDataToSend.append('productImages', image);
            });
        }

        const { productImages, ...newObject } = formData;
        formDataToSend.append('product', JSON.stringify(newObject));

        try {
            const response = await fetch('http://localhost:8081/api/products', {
                method: 'POST',
                body: formDataToSend,
            });

            if (response.ok) {
                setFormData(initialFormData);
                setSelectedDate(null);
                console.log('Data saved successfully!');
            } else {
                console.error('Error:', response.statusText);
            }
        } catch (error) {
            console.error('Error while saving form data to the server', error);
        }
    };

    return (
        <Fragment>
            <div className='flex justify-center'>
                <form className='bg-white p-6 rounded-lg shadow-md w-full max-w-lg' onSubmit={handleSave}>
                    <div className='mb-4'>
                        <label htmlFor='name' className='block text-gray-700'>Product Name:</label>
                        <input
                            type='text'
                            placeholder='Product Name'
                            id='name'
                            name='name'
                            value={formData.name}
                            onChange={handleChange}
                            required
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='price' className='block text-gray-700'>Price:</label>
                        <input
                            type='number'
                            placeholder='Price'
                            id='price'
                            name='price'
                            value={formData.price}
                            onChange={handleChange}
                            required
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='discount' className='block text-gray-700'>Discount:</label>
                        <input
                            type='number'
                            placeholder='Discount'
                            id='discount'
                            name='discount'
                            value={formData.discount}
                            onChange={handleChange}
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label className='block text-gray-700'>Offer End:</label>
                        <DatePicker
                            selected={selectedDate}
                            onChange={handleDateChange}
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='category' className='block text-gray-700'>Category:</label>
                        <select
                            name='category'
                            id='category'
                            value=''
                            onChange={(e) => handleCategorySelection(e.target.value)}
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        >
                            <option value='' disabled>Select a category</option>
                            {initialCategories.map((cat, index) => (
                                <option key={index} value={cat}>
                                    {cat}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='tag' className='block text-gray-700'>Tag:</label>
                        <select
                            name='tag'
                            id='tag'
                            value=''
                            onChange={(e) => handleTagSelection(e.target.value)}
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        >
                            <option value='' disabled>Select a tag</option>
                            <option value='New'>New</option>
                            <option value='Sale'>Sale</option>
                            <option value='Hot'>Hot</option>
                        </select>
                    </div>

                    <div className='mb-4'>
                        <label className='block text-gray-700'>Variation:</label>
                        {formData.variation.map((variation, variationIndex) => (
                            <div key={variationIndex} className='mb-2'>
                                <input
                                    type='text'
                                    placeholder='Color'
                                    value={variation.color}
                                    onChange={(e) => handleColorChange(e, variationIndex)}
                                    className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                                />
                                <input
                                    type='file'
                                    onChange={(e) => handleVariationImageChange(variationIndex, e)}
                                    className='mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-violet-50 file:text-violet-700 hover:file:bg-violet-100'
                                />
                                {variation.size.map((size, sizeIndex) => (
                                    <div key={sizeIndex} className='flex items-center space-x-2 mt-2'>
                                        <input
                                            type='text'
                                            placeholder='Size Name'
                                            value={size.name}
                                            onChange={(e) => handleVariationChange(e, variationIndex, sizeIndex, 'name')}
                                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                                        />
                                        <input
                                            type='number'
                                            placeholder='Stock'
                                            value={size.stock}
                                            onChange={(e) => handleVariationChange(e, variationIndex, sizeIndex, 'stock')}
                                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                                        />
                                    </div>
                                ))}
                                <button
                                    type='button'
                                    onClick={() => handleAddSize(variationIndex)}
                                    className='mt-2 px-4 py-2 bg-blue-500 text-white rounded-md shadow-sm hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500'
                                >
                                    Add Size
                                </button>
                            </div>
                        ))}
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='shortDescription' className='block text-gray-700'>Short Description:</label>
                        <textarea
                            id='shortDescription'
                            name='shortDescription'
                            placeholder='Short Description'
                            value={formData.shortDescription}
                            onChange={handleChange}
                            required
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='fullDescription' className='block text-gray-700'>Full Description:</label>
                        <textarea
                            id='fullDescription'
                            name='fullDescription'
                            placeholder='Full Description'
                            value={formData.fullDescription}
                            onChange={handleChange}
                            required
                            className='mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm'
                        />
                    </div>

                    <div className='mb-4'>
                        <label htmlFor='productImages' className='block text-gray-700'>Product Images:</label>
                        <input
                            type='file'
                            id='productImages'
                            name='productImages'
                            multiple
                            onChange={handleMultipleImagesChange}
                            className='mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-violet-50 file:text-violet-700 hover:file:bg-violet-100'
                        />
                    </div>

                    <button
                        type='submit'
                        className='w-full bg-blue-500 text-white py-2 px-4 rounded-md shadow-sm hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500'
                    >
                        Save Product
                    </button>
                </form>
            </div>
        </Fragment>
    );
};

export default ProductForm;
