import React, { createContext, useContext, useState, ReactNode } from "react";

// Define types for Cart Item and Context
export interface CartItem {
  id: number;
  name: string;
  price: number;
  image: string;
  quantity: number;
  sizes?: string[]; // Make sizes property optional
}

export interface User {
  username: string;
  location: string;
  avatarUrl: string;
}


interface CartContextType {
  cartItems: CartItem[];
  addToCart: (product: CartItem) => void;
}

interface UserContextType {
  user: User | null;
  setUser: React.Dispatch<React.SetStateAction<User | null>>;
}


// Create the context
const CartContext = createContext<CartContextType | undefined>(undefined);
const UserContext = createContext<UserContextType | undefined>(undefined);


// Create the provider component
export const CartProvider = ({ children }: { children: ReactNode }) => {
  const [cartItems, setCartItems] = useState<CartItem[]>([]);
  
  const addToCart = (product: CartItem) => {
    setCartItems((prevItems) => {
      debugger;
      const existingItem = prevItems.find(item => item.id === product.id);
      if (existingItem) {
        return prevItems.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + product.quantity}
            : item
        );
      } else {
        return [...prevItems, { ...product, quantity:product.quantity, sizes: product.sizes }];
      }
    });
  };

  
  return (
    <CartContext.Provider value={{ cartItems, addToCart, user, setUser }}>
      {children}
    </CartContext.Provider>
  );
};




// Custom hook to use the cart context
export const useCart = () => {
  const context = useContext(CartContext);
  if (context === undefined) {
    throw new Error("useCart must be used within a CartProvider");
  }
  return context;
};

export { CartContext };
