import React, { FC, useState, useEffect, ReactElement, useContext } from "react";
import ButtonPrimary from "shared/Button/ButtonPrimary";
import LikeButton from "components/LikeButton";
import AccordionInfo from "./AccordionInfo";
import { StarIcon } from "@heroicons/react/24/solid";
import BagIcon from "components/BagIcon";
import NcInputNumber from "components/NcInputNumber";
import { NoSymbolIcon, ClockIcon, SparklesIcon } from "@heroicons/react/24/outline";
import IconDiscount from "components/IconDiscount";
import Prices from "components/Prices";
import toast from "react-hot-toast";
import SectionSliderProductCard from "components/SectionSliderProductCard";
import Policy from "./Policy";
import ReviewItem from "components/ReviewItem";
import ButtonSecondary from "shared/Button/ButtonSecondary";
import SectionPromo2 from "components/SectionPromo2";
import ModalViewAllReviews from "./ModalViewAllReviews";
import NotifyAddTocart from "components/NotifyAddTocart";
import { useParams } from "react-router-dom";
import { CartContext } from "containers/CartContext";

export interface ProductDetailPageProps {
  className?: string;
}

type ProductVariant = {
  id: number;
  name: string;
  featuredImage: string;
  color: string;
  sizes: string[];
};

interface Product {
  id: number;
  name: string;
  price: number;
  image: string;
  description: string;
  category: string;
  tags: string[];
  link: "/product-detail/";
  variants: ProductVariant[];
  variantType?: "color" | "image";
  sizes?: string[];
  allOfSizes?: string[];
  status?: "New in" | "limited edition" | "Sold Out" | "50% Discount";
}

interface CartItem {
  id: number;
  name: string;
  price: number;
  image: string;
  quantity: number;
  sizes?: string[];
}

function transformProduct(data: any): Product {
  const id = data.id;
  const name = data.name;
  const price = parseFloat(data.price);
  const image = data.images[0];
  const description = data.shortDescription;
  const category = data.category[0];
  const tags = data.tag;
  const link = "/product-detail/";

  const variants = data.variation.map((varItem: any) => ({
    id: varItem.id,
    name: varItem.color,
    featuredImage: varItem.image,
    color: varItem.color,
    sizes: varItem.size.map((size: any) => size.name),
  })) as ProductVariant[];

  const allSizes = data.variation.flatMap((varItem: any) => varItem.size.map((size: any) => size.name));
  const uniqueSizes = Array.from(new Set(allSizes)) as string[];

  let status: "New in" | "limited edition" | "Sold Out" | "50% Discount" | undefined;
  const offerEndDate = new Date(data.offerEnd);
  const currentDate = new Date();
  if (data.new || offerEndDate > currentDate) {
    status = "New in";
  } else if (parseFloat(data.discount) >= 50) {
    status = "50% Discount";
  }

  return {
    id,
    name,
    price,
    image,
    description,
    category,
    tags,
    link,
    variants,
    variantType: "color",
    sizes: uniqueSizes,
    status,
  };
}

const ProductDetailPage: FC<ProductDetailPageProps> = ({ className = "" }): ReactElement => {
  const { productId } = useParams();
  const cartContext = useContext(CartContext);
  if (!cartContext) {
    throw new Error("CartContext is undefined");
  }
  const { addToCart } = cartContext;

  // State variables
  const [images, setImages] = useState<string[]>([]);
  const [sizes, setSizes] = useState<string[]>([]);
  const [variants, setVariants] = useState<ProductVariant[]>([]);
  const [status, setStatus] = useState<"New in" | "limited edition" | "Sold Out" | "50% Discount" | undefined>(undefined);
  const [allOfSizes, setAllOfSizes] = useState<string[]>([]);
  const [variantActive, setVariantActive] = useState(0);
  const [sizeSelected, setSizeSelected] = useState<string>("");
  const [qualitySelected, setQualitySelected] = useState<number>(1);
  const [isOpenModalViewAllReviews, setIsOpenModalViewAllReviews] = useState(false);
  const [transformedProduct, setTransformedProduct] = useState<Product>({} as Product);
  const [isNotifyVisible, setIsNotifyVisible] = useState(false);
  const [price, setPrice] = useState<number>(0);
  const [name, setName] = useState<string>("");

  // Fetch product data on component mount
  useEffect(() => {
    fetchProductsbyId();
  }, [productId]);

  // Fetch product details by id
  const fetchProductsbyId = async () => {
    try {
      const response = await fetch("http://localhost:8081/api/products/" + productId);
      const data = await response.json();
      const transformedProduct = transformProduct(data);
      setTransformedProduct(transformedProduct);
      setImages(transformedProduct.variants.map(variant => variant.featuredImage));
      setSizes(transformedProduct.sizes || []);
      setVariants(transformedProduct.variants || []);
      setStatus(transformedProduct.status);
      setAllOfSizes(transformedProduct.allOfSizes || []);
      setPrice(transformedProduct.price);
      setName(transformedProduct.name);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  const handleAddToCart = () => {
    const selectedSize = sizeSelected || "";
    const cartItem: CartItem = {
      id: transformedProduct.id,
      name: transformedProduct.name,
      price: transformedProduct.price,
      image: transformedProduct.image,
      quantity: qualitySelected,
      sizes: [selectedSize],
    };
    addToCart(cartItem);
    setIsNotifyVisible(true);
    notifyAddTocart();
  };

  // Function to notify adding to cart
  const notifyAddTocart = () => {
    toast.custom(
      (t) => (
        <NotifyAddTocart
          productImage={images[0]}
          qualitySelected={qualitySelected}
          show={t.visible}
          sizeSelected={sizeSelected}
          variantActive={variantActive}
          price={price}
          name={name}
        />
      ),
      { position: "top-right", id: "nc-product-notify", duration: 3000 }
    );
  };

  // Function to render product variants
  const renderVariants = () => {
    if (!variants || !variants.length) {
      return null;
    }

    return (
      <div>
        <label htmlFor="">
          <span className="text-sm font-medium">
            Color:
            <span className="ml-1 font-semibold">
              {variants[variantActive].name}
            </span>
          </span>
        </label>
        <div className="flex mt-3">
          {variants.map((variant, index) => (
            <div
              key={index}
              onClick={() => setVariantActive(index)}
              className={`relative flex-1 max-w-[75px] h-10 sm:h-11 rounded-full border-2 cursor-pointer ${variantActive === index
                ? "border-primary-6000 dark:border-primary-500"
                : "border-transparent"
                }`}
            >
              <div className="absolute inset-0.5 rounded-full overflow-hidden z-0">
                <img
                  src={variant.featuredImage}
                  alt=""
                  className="absolute w-full h-full object-cover"
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  const renderSizeList = () => {
    if (!transformedProduct || !transformedProduct.sizes || !transformedProduct.sizes.length) {
      return null;
    }
    return (
      <div>
        <div className="flex justify-between font-medium text-sm">
          <label htmlFor="">
            <span className="">
              Size:
              <span className="ml-1 font-semibold">{sizeSelected}</span>
            </span>
          </label>
          <a
            target="_blank"
            rel="noopener noreferrer"
            href="##"
            className="text-primary-6000 hover:text-primary-500"
          >
            See sizing chart
          </a>
        </div>
        <div className="grid grid-cols-5 sm:grid-cols-7 gap-2 mt-3">
          {transformedProduct.sizes.map((size, index) => {
            const isActive = size === sizeSelected;
            return (
              <div
                key={index}
                onClick={() => setSizeSelected(size)}
                className={`relative h-10 sm:h-11 rounded-2xl border flex items-center justify-center text-sm sm:text-base uppercase font-semibold select-none overflow-hidden z-0 cursor-pointer ${isActive
                  ? "bg-black shadow-lg border-black text-white"
                  : "border-neutral-300 dark:border-neutral-6000"
                  }`}
              >
                <div className="absolute inset-0.5 rounded-xl bg-white dark:bg-neutral-900"></div>
                <span className="relative z-10">{size}</span>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  return (
    <div className={`nc-ProductDetailPage ${className}`} data-nc-id="ProductDetailPage">
      <main className="container mt-5 lg:mt-11">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 lg:gap-12">
          {/* IMAGE SLIDER */}
          <div className="product-main-slider">
            {images.length ? (
              <img src={images[variantActive]} alt={transformedProduct.name} />
            ) : (
              <div>No Image Available</div>
            )}
          </div>

          {/* DETAILS */}
          <div className="product-main-details">
            <h2 className="text-2xl lg:text-3xl font-semibold">
              {transformedProduct.name}
            </h2>
            <Prices price={price} className="mt-3" />
            {status && <span className="mt-1 text-sm text-green-600">{status}</span>}

            <div className="mt-6">
              <h3 className="text-lg font-medium">Variants</h3>
              {renderVariants()}
            </div>

            <div className="mt-6">
              <h3 className="text-lg font-medium">Sizes</h3>
              {renderSizeList()}
            </div>

            <div className="mt-6">
              <div className="flex flex-row items-center space-x-3">
                <NcInputNumber
                  defaultValue={1}
                  onChange={setQualitySelected}
                  className="w-20"
                  min={1}
                />
                <ButtonPrimary className="flex-1" onClick={handleAddToCart}>
                  Add to Cart
                </ButtonPrimary>
              </div>
            </div>
          </div>
        </div>

        <SectionPromo2 className="mt-8 lg:mt-14" />

        {/* DESCRIPTION */}
        <AccordionInfo className="mt-10" product={transformedProduct} />

        {/* REVIEWS */}
        <div className="mt-14">
          <h3 className="text-lg font-medium">Customer Reviews</h3>
          <div className="mt-3">
            {transformedProduct.reviews?.length ? (
              transformedProduct.reviews.map((review, index) => (
                <ReviewItem key={index} review={review} />
              ))
            ) : (
              <p>No reviews yet</p>
            )}
          </div>
          <ButtonSecondary
            className="mt-5"
            onClick={() => setIsOpenModalViewAllReviews(true)}
          >
            View All Reviews
          </ButtonSecondary>
        </div>

        <ModalViewAllReviews
          show={isOpenModalViewAllReviews}
          onCloseModal={() => setIsOpenModalViewAllReviews(false)}
        />
      </main>
    </div>
  );
};

export default ProductDetailPage;
