import React, { createContext, useContext, useState} from "react";

// Define types for Cart Item and Context
interface Product {
  id: number;
  name: string;
  price: number;
  image: string;
  quantity: number;
}

interface CartContextType {
  cartItems: Product[];
  addToCart: (item: Product) => void;
  clearCart: () => void;
  cart: Product[]; // Add cart property
  setCart: (cart: Product[]) => void; // Add setCart property
}

// Create the context
const CartContext = createContext<CartContextType>({
  cartItems: [],
  addToCart: () => {},
  clearCart: () => {},
});

export const useCart = () => useContext(CartContext);
export const CartProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [cartItems, setCartItems] = useState<Product[]>([]);
  const [cart, setCart] = useState<Product[]>([]);

  const addToCart = (item: Product) => {
    console.log('Adding item to cart:', item);
    setCartItems(prevItems => [...prevItems, item]);
    setCart(prevCart => [...prevCart, item]); // Update cart state
  };
  const clearCart = () => {
    setCartItems([]);
    setCart([]); // Clear cart state
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart, clearCart, cart, setCart }}>
      {children}
    </CartContext.Provider>
  );
};
