import React, { FC } from "react";
import { Link, useNavigate } from "react-router-dom";
import NcImage from "shared/NcImage/NcImage";
import LikeButton from "./LikeButton";
import Prices from "./Prices";
import { ArrowsPointingOutIcon } from "@heroicons/react/24/outline";
import ButtonPrimary from "shared/Button/ButtonPrimary";
import ButtonSecondary from "shared/Button/ButtonSecondary";
import BagIcon from "./BagIcon";
import toast from "react-hot-toast";
import { Transition } from "@headlessui/react";
import ModalQuickView from "./ModalQuickView";
import ProductStatus from "./ProductStatus";

export interface ProductCardProps {
  className?: string;
  data?: Product;
  isLiked?: boolean;
  onProductClick: (product: Product) => void; // Callback function to pass data to parent component
}

const ProductCard: FC<ProductCardProps> = ({
  className = "",
  data,
  isLiked,
  onProductClick,
}) => {
  if (!data) {
    return null;
  }

  const navigate = useNavigate();
  const [variantActive, setVariantActive] = React.useState(0);
  const [showModalQuickView, setShowModalQuickView] = React.useState(false);

  const handleClick = () => {
    onProductClick(data); // Pass the product data to the parent component when clicked
  };

  // Destructure product data
  const { name, price, description, sizes, variants, variantType, status, image } = data;

  const notifyAddTocart = ({ size }: { size?: string }) => {
    // Notification logic
  };

  // Render component
  return (
    <>
      <div
        className={`nc-ProductCard relative flex flex-col bg-transparent ${className}`}
        data-nc-id="ProductCard"
        onClick={handleClick}
      >
        <Link to={"/product-detail"} className="absolute inset-0"></Link>

        <div className="relative flex-shrink-0 bg-slate-50 dark:bg-slate-300 rounded-3xl overflow-hidden z-1 group">
          <Link to={"/product-detail"} className="block">
            <NcImage
              containerClassName="flex aspect-w-11 aspect-h-12 w-full h-0"
              src={"http://localhost:8081/images/" + image[0]}
              className="object-cover w-full h-full drop-shadow-xl"
            />
          </Link>

          <ProductStatus status={status} />

          {/* LikeButton and other elements */}
        </div>

        {/* Rest of the component */}
      </div>

      {/* QUICKVIEW */}
      <ModalQuickView
        // ModalQuickView props
      />
    </>
  );
};

export default ProductCard;
