import React, { useState, Fragment, ChangeEvent, FormEvent } from 'react';
import { useLocation } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

interface Size {
    name: string;
    stock: string;
}

interface Variation {
    color: string;
    size: Size[];
    image?: File | null;
}

interface FormData {
    sku: string;
    name: string;
    price: number;
    discount: number;
    offerEnd: Date | null;
    new: boolean;
    rating: number;
    saleCount: number;
    category: string[];
    tag: string[];
    variation: Variation[];
    shortDescription: string;
    fullDescription: string;
    productImages?: File[];
}

const ProductForm: React.FC = () => {
    const { pathname } = useLocation();

    const initialFormData: FormData = {
        sku: 'asdf123',
        name: '',
        price: 0,
        discount: 0,
        offerEnd: null,
        new: false,
        rating: 0,
        saleCount: 0,
        category: [],
        tag: [],
        variation: [
            {
                color: '',
                size: [{ name: '', stock: '' }],
            },
        ],
        shortDescription: '',
        fullDescription: '',
    };

    const initialCategories = ['fashion', 'men'];

    const [formData, setFormData] = useState<FormData>(initialFormData);
    const [selectedDate, setSelectedDate] = useState<Date | null>(null);

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: name === 'new' ? value === 'true' : value,
        }));
    };

    const handleVariationImageChange = (variationIndex: number, e: ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (file) {
            setFormData((prevData) => {
                const updatedVariation = [...prevData.variation];
                updatedVariation[variationIndex].image = file;
                return {
                    ...prevData,
                    variation: updatedVariation,
                };
            });
        }
    };

    const handleMultipleImagesChange = (e: ChangeEvent<HTMLInputElement>) => {
        const files = e.target.files;
        if (files && files.length > 0) {
            const fileData: File[] = Array.from(files);
            setFormData((prevData) => ({
                ...prevData,
                productImages: fileData,
            }));
        }
    };

    const handleVariationChange = (
        e: ChangeEvent<HTMLInputElement>,
        variationIndex: number,
        sizeIndex: number,
        property: keyof Size
    ) => {
        const { value } = e.target;
        setFormData((prevData) => {
            const updatedVariation = [...prevData.variation];
            if (!updatedVariation[variationIndex].size) {
                updatedVariation[variationIndex].size = [];
            }
            while (updatedVariation[variationIndex].size.length <= sizeIndex) {
                updatedVariation[variationIndex].size.push({ name: '', stock: '' });
            }
            updatedVariation[variationIndex].size[sizeIndex][property] = value;
            return {
                ...prevData,
                variation: updatedVariation,
            };
        });
    };

    const handleCategorySelection = (selectedCategory: string | null) => {
        if (selectedCategory) {
            setFormData((prevData) => ({
                ...prevData,
                category: [...prevData.category, selectedCategory],
            }));
        }
    };

    const handleTagSelection = (selectedTag: string | null) => {
        if (selectedTag) {
            setFormData((prevData) => ({
                ...prevData,
                tag: [...prevData.tag, selectedTag],
            }));
        }
    };

    const handleDateChange = (date: Date | null) => {
        setSelectedDate(date);
        setFormData((prevData) => ({
            ...prevData,
            offerEnd: date,
        }));
    };

    const handleColorChange = (e: ChangeEvent<HTMLInputElement>, variationIndex: number) => {
        const { value } = e.target;
        const updatedFormData = { ...formData };
        if (updatedFormData.variation && updatedFormData.variation.length > variationIndex) {
            updatedFormData.variation[variationIndex].color = value;
            setFormData(updatedFormData);
        } else {
            console.error('Variation index out of range or formData.variation is undefined');
        }
    };

    const handleSave = async (e: FormEvent) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formData.variation.forEach((variation) => {
            if (variation.image) {
                formDataToSend.append('variationImage', variation.image);
                variation.image = null;
            }
        });

        if (formData.productImages && formData.productImages.length > 0) {
            formData.productImages.forEach((image) => {
                formDataToSend.append('productImages', image);
            });
        }

        const { productImages, ...newObject } = formData;
        formDataToSend.append('product', JSON.stringify(newObject));

        try {
            const response = await fetch('http://localhost:8081/api/products', {
                method: 'POST',
                body: formDataToSend,
            });

            if (response.ok) {
                setFormData(initialFormData);
                setSelectedDate(null);
                console.log('Data saved successfully!');
            } else {
                console.error('Error:', response.statusText);
            }
        } catch (error) {
            console.error('Error while saving form data to the server', error);
        }
    };

    return (
        <Fragment>
            <div className='center-container'>
                <form className='product-form' onSubmit={handleSave}>
                    <div className='product-body'>
                        <div className='form-body-child'>
                            <div className='col-md-10'>
                                <label htmlFor='name'>Product Name:</label>
                                <input
                                    type='text'
                                    placeholder='Product Name'
                                    id='name'
                                    name='name'
                                    value={formData.name}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                        </div>

                        <div className='form-body-child'>
                            <div className='col-md-10'>
                                <label htmlFor='price'>Price:</label>
                                <input
                                    type='number'
                                    placeholder='Price'
                                    id='price'
                                    name='price'
                                    value={formData.price}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                        </div>

                        <div className='form-body-child'>
                            <div className='col-md-10'>
                                <label htmlFor='discount'>Discount:</label>
                                <input
                                    type='number'
                                    placeholder='Discount'
                                    id='discount'
                                    name='discount'
                                    value={formData.discount}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>

                        <div className='form-body-child'>
                            <div className='col-md-10'>
                                <label htmlFor='rating'>Rating:</label>
                                <input
                                    type='number'
                                    placeholder='Rating'
                                    id='rating'
                                    name='rating'
                                    value={formData.rating}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>

                        <div className='form-body-child'>
                            <div className='col-md-10'>
                                <label htmlFor='saleCount'>Sale Count:</label>
                                <input
                                    type='text'
                                    placeholder='Sale Count'
                                    id='saleCount'
                                    name='saleCount'
                                    value={formData.saleCount}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>
                        
                        <div className='form-body-child'>
                            <label htmlFor='offerEnd'>Offer End Date:</label>
                            <div className='col-md-12'>
                                <DatePicker
                                    placeholderText='Select Date'
                                    selected={selectedDate}
                                    onChange={handleDateChange}
                                    showTimeSelect
                                    wrapperClassName='date'
                                    timeIntervals={15}
                                    timeCaption='Time'
                                    dateFormat='MMMM d, yyyy h:mm aa'
                                    required
                                />
                            </div>
                        </div>

                        <div className='form-body-child'>
                            <div className='col-md-10'>
                                <label htmlFor='shortDescription'>Short Description:</label>
                                <input
                                    type='text'
                                    placeholder='Short Description'
                                    id='shortDescription'
                                    name='shortDescription'
                                    value={formData.shortDescription}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>

                        <div className='form-body-child'>
                            <div className='col-md-10'>
                                <label htmlFor='fullDescription'>Full Description:</label>
                                <input
                                    type='text'
                                    placeholder='Full Description'
                                    id='fullDescription'
                                    name='fullDescription'
                                    value={formData.fullDescription}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>

                        <div className='form-body-child'>
                            <div className='col-md-10'>
                                <label htmlFor='productImages'>Product Images:</label>
                                <input
                                    type='file'
                                    multiple
                                    id='productImages'
                                    name='productImages'
                                    onChange={handleMultipleImagesChange}
                                />
                            </div>
                        </div>

                        {formData.variation.map((variation, variationIndex) => (
                            <div key={variationIndex} className='form-body-child'>
                                <div className='col-md-10'>
                                    <label htmlFor={`variation${variationIndex}Color`}>Variation Color:</label>
                                    <input
                                        type='text'
                                        placeholder='Color'
                                        id={`variation${variationIndex}Color`}
                                        name={`variation${variationIndex}Color`}
                                        value={variation.color}
                                        onChange={(e) => handleColorChange(e, variationIndex)}
                                    />
                                </div>
                                <div className='col-md-10'>
                                    <label htmlFor={`variation${variationIndex}Image`}>Variation Image:</label>
                                    <input
                                        type='file'
                                        id={`variation${variationIndex}Image`}
                                        name={`variation${variationIndex}Image`}
                                        onChange={(e) => handleVariationImageChange(variationIndex, e)}
                                    />
                                </div>

                                {variation.size.map((size, sizeIndex) => (
                                    <div key={sizeIndex} className='form-body-child'>
                                        <div className='col-md-5'>
                                            <label htmlFor={`variation${variationIndex}Size${sizeIndex}Name`}>Size Name:</label>
                                            <input
                                                type='text'
                                                placeholder='Size'
                                                id={`variation${variationIndex}Size${sizeIndex}Name`}
                                                name={`variation${variationIndex}Size${sizeIndex}Name`}
                                                value={size.name}
                                                onChange={(e) =>
                                                    handleVariationChange(e, variationIndex, sizeIndex, 'name')
                                                }
                                            />
                                        </div>
                                        <div className='col-md-5'>
                                            <label htmlFor={`variation${variationIndex}Size${sizeIndex}Stock`}>Stock:</label>
                                            <input
                                                type='text'
                                                placeholder='Stock'
                                                id={`variation${variationIndex}Size${sizeIndex}Stock`}
                                                name={`variation${variationIndex}Size${sizeIndex}Stock`}
                                                value={size.stock}
                                                onChange={(e) =>
                                                    handleVariationChange(e, variationIndex, sizeIndex, 'stock')
                                                }
                                            />
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ))}

                        <div className='form-body-child'>
                            <div className='col-md-10'>
                                <label htmlFor='category'>Category:</label>
                                <input
                                    type='text'
                                    placeholder='Category'
                                    id='category'
                                    name='category'
                                    value={formData.category.join(', ')}
                                    readOnly
                                />
                                {initialCategories.map((category, index) => (
                                    <button type='button' key={index} onClick={() => handleCategorySelection(category)}>
                                        {category}
                                    </button>
                                ))}
                            </div>
                        </div>

                        <div className='form-body-child'>
                            <div className='col-md-10'>
                                <label htmlFor='tag'>Tags:</label>
                                <input
                                    type='text'
                                    placeholder='Tags'
                                    id='tag'
                                    name='tag'
                                    value={formData.tag.join(', ')}
                                    readOnly
                                />
                            </div>
                        </div>
                    </div>
                    <button type='submit'>Save</button>
                </form>
            </div>
        </Fragment>
    );
};

export default ProductForm;
