import React, { FC, useEffect, useState } from "react";
import { MinusIcon, PlusIcon } from "@heroicons/react/24/solid";
import { useCart } from "containers/CartContext";

export interface NcInputNumberProps {
  className?: string;
  productId: number; // Add productId prop to identify the product
  min?: number;
  max?: number;
  onChange?: (value: number) => void;
  label?: string;
  desc?: string;
}

const NcInputNumber: FC<NcInputNumberProps> = ({
  className = "w-full",
  productId,
  min = 1,
  max = 99,
  onChange,
  label,
  desc,
}) => {
  const { cartItems } = useCart();
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    // Find the product in cartItems based on productId
    const productInCart = cartItems.find(item => item.id === productId);
    if (productInCart) {
      setQuantity(productInCart.quantity); // Set quantity from cart
    } else {
      setQuantity(1); // Default quantity if product not found in cart
    }
  }, [cartItems, productId]);

  const handleClickDecrement = () => {
    if (min >= quantity) return;
    const newQuantity = quantity - 1;
    setQuantity(newQuantity);
    onChange && onChange(newQuantity);
  };
  
  const handleClickIncrement = () => {
    if (max && max <= quantity) return;
    const newQuantity = quantity + 1;
    setQuantity(newQuantity);
    onChange && onChange(newQuantity);
  };

  // Rest of the component remains the same...

  return (
    <div
      className={`nc-NcInputNumber flex items-center justify-between space-x-5 ${className}`}
    >
      {label && renderLabel()}

      <div
        className={`nc-NcInputNumber__content flex items-center justify-between w-[104px] sm:w-28`}
      >
        {/* Rest of the component remains the same... */}
      </div>
    </div>
  );
};

export default NcInputNumber;
